export function ReadableStreamFrom<R>(
  source: Iterable<R> | AsyncIterable<R> | ReadableStreamLike<R>
): DefaultReadableStream<R> {
  if (isReadableStreamLike(source)) {
    return ReadableStreamFromDefaultReader(source.getReader());
