export enum CheckRepoActions {
   BARE = 'bare',
   IN_TREE = 'tree',
   IS_REPO_ROOT = 'root',
}

const onError: StringTask<boolean>['onError'] = ({ exitCode }, error, done, fail) => {
   if (exitCode === ExitCodes.UNCLEAN && isNotRepoMessage(error)) {
      return done(Buffer.from('false'));
   }

   fail(error);
};

const parser: StringTask<boolean>['parser'] = (text) => {
   return text.trim() === 'true';
};

export function checkIsRepoTask(action: Maybe<CheckRepoActions>): StringTask<boolean> {
   switch (action) {
      case CheckRepoActions.BARE:
         return checkIsBareRepoTask();
      case CheckRepoActions.IS_REPO_ROOT:
         return checkIsRepoRootTask();
   }

   const commands = ['rev-parse', '--is-inside-work-tree'];

   return {
      commands,
      format: 'utf-8',
      onError,
      parser,
   };
}

export function checkIsRepoRootTask(): StringTask<boolean> {
   const commands = ['rev-parse', '--git-dir'];

   return {
      commands,
      format: 'utf-8',
      onError,
      parser(path) {
         return /^\.(git)?$/.test(path.trim());
      },
   };
}

export function checkIsBareRepoTask(): StringTask<boolean> {
   const commands = ['rev-parse', '--is-bare-repository'];

   return {
      commands,
      format: 'utf-8',
      onError,
      parser,
   };
}

function isNotRepoMessage(error: Error): boolean {
   return /(Not a git repository|Kein Git-Repository)/i.test(String(error));
}
import { CleanSummary } from '../../../typings';
import { toLinesWithContent } from '../utils';

export class CleanResponse implements CleanSummary {
   public paths: string[] = [];
   public files: string[] = [];
   public folders: string[] = [];

   constructor(public readonly dryRun: boolean) {}
}

const removalRegexp = /^[a-z]+\s*/i;
const dryRunRemovalRegexp = /^[a-z]+\s+[a-z]+\s*/i;
const isFolderRegexp = /\/$/;

export function cleanSummaryParser(dryRun: boolean, text: string): CleanSummary {
   const summary = new CleanResponse(dryRun);
   const regexp = dryRun ? dryRunRemovalRegexp : removalRegexp;

   toLinesWithContent(text).forEach((line) => {
      const removed = line.replace(regexp, '');

      summary.paths.push(removed);
      (isFolderRegexp.test(removed) ? summary.folders : summary.files).push(removed);
   });

   return summary;
}
import { TaskConfigurationError } from '../errors/task-configuration-error';
import type { BufferTask, EmptyTaskParser, SimpleGitTask, StringTask } from '../types';

export const EMPTY_COMMANDS: [] = [];

export type EmptyTask = {
   commands: typeof EMPTY_COMMANDS;
   format: 'empty';
   parser: EmptyTaskParser;
   onError?: undefined;
};

export function adhocExecTask(parser: EmptyTaskParser): EmptyTask {
   return {
      commands: EMPTY_COMMANDS,
      format: 'empty',
      parser,
   };
}

export function configurationErrorTask(error: Error | string): EmptyTask {
   return {
      commands: EMPTY_COMMANDS,
      format: 'empty',
      parser() {
         throw typeof error === 'string' ? new TaskConfigurationError(error) : error;
      },
   };
}

export function straightThroughStringTask(commands: string[], trimmed = false): StringTask<string> {
   return {
      commands,
      format: 'utf-8',
      parser(text) {
         return trimmed ? String(text).trim() : text;
      },
   };
}

export function straightThroughBufferTask(commands: string[]): BufferTask<any> {
   return {
      commands,
      format: 'buffer',
      parser(buffer) {
         return buffer;
      },
   };
}

export function isBufferTask<R>(task: SimpleGitTask<R>): task is BufferTask<R> {
   return task.format === 'buffer';
}

export function isEmptyTask<R>(task: SimpleGitTask<R>): task is EmptyTask {
   return task.format === 'empty' || !task.commands.length;
}
import { CleanSummary } from '../../../typings';
import { cleanSummaryParser } from '../responses/CleanSummary';
import { Maybe, StringTask } from '../types';
import { asStringArray } from '../utils';
import { configurationErrorTask } from './task';

export const CONFIG_ERROR_INTERACTIVE_MODE = 'Git clean interactive mode is not supported';
export const CONFIG_ERROR_MODE_REQUIRED = 'Git clean mode parameter ("n" or "f") is required';
export const CONFIG_ERROR_UNKNOWN_OPTION = 'Git clean unknown option found in: ';

/**
 * All supported option switches available for use in a `git.clean` operation
 */
export enum CleanOptions {
   DRY_RUN = 'n',
   FORCE = 'f',
   IGNORED_INCLUDED = 'x',
   IGNORED_ONLY = 'X',
   EXCLUDING = 'e',
   QUIET = 'q',
   RECURSIVE = 'd',
}

/**
 * The two modes `git.clean` can run in - one of these must be supplied in order
 * for the command to not throw a `TaskConfigurationError`
 */
export type CleanMode = CleanOptions.FORCE | CleanOptions.DRY_RUN;

const CleanOptionValues: Set<string> = new Set([
   'i',
   ...asStringArray(Object.values(CleanOptions as any)),
]);

export function cleanWithOptionsTask(mode: CleanMode | string, customArgs: string[]) {
   const { cleanMode, options, valid } = getCleanOptions(mode);

   if (!cleanMode) {
      return configurationErrorTask(CONFIG_ERROR_MODE_REQUIRED);
   }

   if (!valid.options) {
      return configurationErrorTask(CONFIG_ERROR_UNKNOWN_OPTION + JSON.stringify(mode));
   }

   options.push(...customArgs);

   if (options.some(isInteractiveMode)) {
      return configurationErrorTask(CONFIG_ERROR_INTERACTIVE_MODE);
   }

   return cleanTask(cleanMode, options);
}

export function cleanTask(mode: CleanMode, customArgs: string[]): StringTask<CleanSummary> {
   const commands: string[] = ['clean', `-${mode}`, ...customArgs];

   return {
      commands,
      format: 'utf-8',
      parser(text: string): CleanSummary {
         return cleanSummaryParser(mode === CleanOptions.DRY_RUN, text);
      },
   };
}

export function isCleanOptionsArray(input: string[]): input is CleanOptions[] {
   return Array.isArray(input) && input.every((test) => CleanOptionValues.has(test));
}

function getCleanOptions(input: string) {
   let cleanMode: Maybe<CleanMode>;
   let options: string[] = [];
   let valid = { cleanMode: false, options: true };

   input
      .replace(/[^a-z]i/g, '')
      .split('')
      .forEach((char) => {
         if (isCleanMode(char)) {
            cleanMode = char;
            valid.cleanMode = true;
         } else {
            valid.options = valid.options && isKnownOption((options[options.length] = `-${char}`));
         }
      });

   return {
      cleanMode,
      options,
      valid,
   };
}

function isCleanMode(cleanMode?: string): cleanMode is CleanMode {
   return cleanMode === CleanOptions.FORCE || cleanMode === CleanOptions.DRY_RUN;
}

function isKnownOption(option: string): boolean {
   return /^-[a-z]$/i.test(option) && CleanOptionValues.has(option.charAt(1));
}

function isInteractiveMode(option: string): boolean {
   if (/^-[^\-]/.test(option)) {
      return option.indexOf('i') > 0;
   }

   return option === '--interactive';
}
import { ConfigGetResult, ConfigListSummary, ConfigValues } from '../../../typings';
import { last, splitOn } from '../utils';

export class ConfigList implements ConfigListSummary {
   public files: string[] = [];
   public values: { [fileName: string]: ConfigValues } = Object.create(null);

   private _all: ConfigValues | undefined;

   public get all(): ConfigValues {
      if (!this._all) {
         this._all = this.files.reduce((all: ConfigValues, file: string) => {
            return Object.assign(all, this.values[file]);
         }, {});
      }

      return this._all;
   }

   public addFile(file: string): ConfigValues {
      if (!(file in this.values)) {
         const latest = last(this.files);
         this.values[file] = latest ? Object.create(this.values[latest]) : {};

         this.files.push(file);
      }

      return this.values[file];
   }

   public addValue(file: string, key: string, value: string) {
      const values = this.addFile(file);

      if (!values.hasOwnProperty(key)) {
         values[key] = value;
      } else if (Array.isArray(values[key])) {
         (values[key] as string[]).push(value);
      } else {
         values[key] = [values[key] as string, value];
      }

      this._all = undefined;
   }
}

export function configListParser(text: string): ConfigList {
   const config = new ConfigList();

   for (const item of configParser(text)) {
      config.addValue(item.file, String(item.key), item.value);
   }

   return config;
}

export function configGetParser(text: string, key: string): ConfigGetResult {
   let value: string | null = null;
   const values: string[] = [];
   const scopes: Map<string, string[]> = new Map();

   for (const item of configParser(text, key)) {
      if (item.key !== key) {
         continue;
      }

      values.push((value = item.value));

      if (!scopes.has(item.file)) {
         scopes.set(item.file, []);
      }

      scopes.get(item.file)!.push(value);
   }

   return {
      key,
      paths: Array.from(scopes.keys()),
      scopes,
      value,
      values,
   };
}

function configFilePath(filePath: string): string {
   return filePath.replace(/^(file):/, '');
}

function* configParser(text: string, requestedKey: string | null = null) {
   const lines = text.split('\0');

   for (let i = 0, max = lines.length - 1; i < max; ) {
      const file = configFilePath(lines[i++]);

      let value = lines[i++];
      let key = requestedKey;

      if (value.includes('\n')) {
         const line = splitOn(value, '\n');
         key = line[0];
         value = line[1];
      }

      yield { file, key, value };
   }
}
import type { ConfigGetResult, ConfigListSummary, SimpleGit } from '../../../typings';
import { configGetParser, configListParser } from '../responses/ConfigList';
import type { SimpleGitApi } from '../simple-git-api';
