   };

   const onSpawnBefore: SimpleGitPlugin<'spawn.before'> = {
      type: 'spawn.before',
      action(_data, context) {
         if (signal.aborted) {
            context.kill(new GitPluginError(undefined, 'abort', 'Abort already signaled'));
         }
      },
   };

   return [onSpawnBefore, onSpawnAfter];
